from app.mktcore.mdlimports import *

class MKT_OVERDRAFT_WRITE_OFF(exmodel):
	ID 					= 	db.Column(db.String(14), primary_key=True, index=True)
	CustomerID 			= 	db.Column(db.String(14))
	Account				= 	db.Column(db.String(14))
	Currency 			=	db.Column(db.String(3))
	TranDate			=	db.Column(db.String(10))
	AIRAmount 			=	db.Column(db.Numeric(25, 9))
	OutstandingAmount	=	db.Column(db.Numeric(25, 9))
	TotWriteOffAmount	=	db.Column(db.Numeric(25, 9))
	AmountForRecovery	=	db.Column(db.Numeric(25, 9))

