from app.mktcore.mdlimports import *

class MKT_PAST_DUE(exmodel):
	ID 					= 	db.Column(db.String(15), primary_key=True, index=True)
	LoanID 				= 	db.Column(db.String(13))
	Customer 			= 	db.Column(db.String(12))
	Currency 			=	db.Column(db.String(3))
	Category 			=	db.Column(db.String(10))
	TotODAmount 		=	db.Column(db.Numeric(25, 9))
	TotPrincipalDue		=	db.Column(db.Numeric(25, 9))
	TotInterestDue		=	db.Column(db.Numeric(25, 9))
	TotPenaltyDue		=	db.Column(db.Numeric(25, 9))
	TotChargeDue		=	db.Column(db.Numeric(25, 9))
	PenaltyType 		=	db.Column(db.String(1))
	PenaltyRate 		= 	db.Column(db.String(200))

