	PenaltyDue 			=	db.Column(db.Numeric(25, 9))
	OutPenAmount 		=	db.Column(db.Numeric(25, 9))
	ChargeDue 			=	db.Column(db.Numeric(25, 9))
	OutChgAmount 		=	db.Column(db.Numeric(25, 9))
	NextRunDate 		=	db.Column(db.String(20))

