class MKT_PENALTY(exmodel):
	ID 					= 	db.Column(db.String(20), primary_key=True, index=True)
	LoanID 				=	db.Column(db.String(14))
	CustomerName 		=	db.Column(db.String(32))
	Currency 			=	db.Column(db.String(3))
	PDID 				=	db.Column(db.String(23))
	DueDate				=	db.Column(db.String(10))
	TranDate			=	db.Column(db.String(10))
	Operation 			=	db.Column(db.String(3))
	Amount 				=	db.Column(db.Numeric(25, 9)) #waive penalty amount
	Note 				=	db.Column(db.String(50))
	InterestAmount 		=	db.Column(db.Numeric(25,9))	 #waive interest amount
	PDWaive 			= 	db.Column(db.String(1), default='Y')	 #For noted whether this record is waive pd or current (interest), Y=PD, N=Curr
	Reference 			= 	db.Column(db.String(20))

