from app.mktcore.mdlimports import *

class MKT_PD_PARAM(exmodel):
	ID 				= 	db.Column(db.String(6), primary_key=True, index=True)
	PRE 			= 	db.Column(db.String(2))
	GRA 			= 	db.Column(db.String(2))
	PDO 			= 	db.Column(db.String(2))
	NAB 			= 	db.Column(db.String(3))
	RepOrder 		=	db.Column(db.String(15))
	FWOFRepOrder 	=	db.Column(db.String(15))
	PenType 		=	db.Column(db.String(1))
	PenaltyRate		= 	db.Column(db.String(200))
	PenCalBase 		=	db.Column(db.String(15))
	PenOption 		=	db.Column(db.String(1))
	SuspPLNAB 		=	db.Column(db.String(1))
	RevPLNAB 		=	db.Column(db.String(1))
	SuspendDrCat 	=	db.Column(db.String(15))
	SuspendCrCat 	=	db.Column(db.String(15))
	RepMethod 		=	db.Column(db.String(1))
	ODPenaltyCat 	=	db.Column(db.String(15))
	PenaltyRecCat 	=	db.Column(db.String(15))
	PenaltyAmend 	=	db.Column(db.String(15))
	PenaltyTerminate=	db.Column(db.String(15))
	ChargeIncCat	=	db.Column(db.String(15))
	ChargeRecCat	=	db.Column(db.String(15))
	RecoveryCat 	=	db.Column(db.String(15))
	WOFClass 		=	db.Column(db.String(2))
	WOFDay 			=	db.Column(db.String(2))
	SocialLoanWriteOff	=	db.Column(db.String(15))
	PreTermination 		=	db.Column(db.String(100))
	AmendmentPenalty	=	db.Column(db.String(25))

