from app.mktcore.mdlimports import *


class MKT_PO_CUSTOMER(exmodel):
	ID				=	db.Column(db.String(12), primary_key=True)
	FirstNameEn		=	db.Column(db.String(35))
	LastNameEn		=	db.Column(db.String(35))
	FirstNameKh		=	db.Column(db.String(35))
	LastNameKh		=	db.Column(db.String(35))
	Gender			=	db.Column(db.String(6))
	
	Province		=	db.Column(db.String(6))
	District		=	db.Column(db.String(10))
	Commune 		=	db.Column(db.String(14))
	Village			=	db.Column(db.String(18))

	HouseNo			=	db.Column(db.String(50))
	Street			=	db.Column(db.String(40))
	Mobile1 		=	db.Column(db.String(15))
	Mobile2			=	db.Column(db.String(15))
	Email			=	db.Column(db.String(50))
	ProductInterest =	db.Column(db.String(3))
	DateOfContact	=	db.Column(db.String(10))
	POStatus		=	db.Column(db.String(3))
	NextFollowDate 	=	db.Column(db.String(10))
	Note 			=	db.Column(db.String(100))
	AssignBranch	=	db.Column(db.String(6))
	AssignDate		=	db.Column(db.String(50))
	AssignTo		=	db.Column(db.String(35))	
	Block           =   db.Column(db.String(1))
	BlockReason     =   db.Column(db.Text)
	PriceOfProperty	= 	db.Column(db.Numeric(25, 9))
	LoanAmount		= 	db.Column(db.Numeric(25, 9))
	Currency		= 	db.Column(db.String(3))
	ExistingClient  = 	db.Column(db.String(3))
	UserID 			=	db.Column(db.String(35))

