from flask      import jsonify
from ..models   import *

def getQuickSearchItems():
    Items = MKT_QUICK_SEARCH_ITEM.query.all()
    QuickSearchItems = []
    for Item in Items:
        QuickSearchItems.append({ 
            "id" : Item.ItemID, 
            "text" : Item.ItemDesc, 
            "url" : Item.RequestUrl, 
            "action_url" : Item.ActionUrl, 
            "icon": Item.Icon 
        })
    return QuickSearchItems