from app.mktcore.mdlimports import *

class MKT_REJECTED(exmodel):
	ID				=	db.Column(db.String(12), primary_key=True)
	CustomerID		=	db.Column(db.String(12))
	LoanAppID		=	db.Column(db.String(12))
	Rejected 		=	db.Column(db.String(1))
	RejectedBy		=	db.Column(db.String(1))
	RejectedReason	= 	db.Column(db.Text())
	RejectedDate	= 	db.Column(db.String(10))
	ReasonType 		= 	db.Column(db.String(3))

