from app.mktcore.mdlimports import *

class MKT_REP_SCHEDULE(exmodel):
	ID 					= 	db.Column(db.String(20), primary_key=True, index=True)
	LoanID 				= 	db.Column(db.String(13), index=True)
	No 					= 	db.Column(db.Integer)
	CollectionDate 		= 	db.Column(db.String(40))
	Principal 			= 	db.Column(db.Numeric(25, 9))
	Interest 			= 	db.Column(db.Numeric(25, 9))
	Charge 				= 	db.Column(db.Numeric(25, 9))
	Balance 			= 	db.Column(db.Numeric(25, 9))
	NumDay 				= 	db.Column(db.Integer)
	RepStatus 			= 	db.Column(db.String(1))
	PartPaidAmt 		= 	db.Column(db.Numeric(25, 9))

