from app.mktcore.mdlimports import *

class MKT_SAVING_PLAN(exmodel):
	ID 				= 	db.Column(db.String(20), primary_key=True, index=True)
	CustomerID  	= 	db.Column(db.String(10), index=True)
	AccProduct  	= 	db.Column(db.String(10))
	SavingAccount  	= 	db.Column(db.String(20)) #saving plan account
	GeneralAccount 	=	db.Column(db.String(20)) #Voluntary Account
	AccProduct  	= 	db.Column(db.String(10))
	OpeningDate		=	db.Column(db.String(15))
	MaturityDate	=	db.Column(db.String(15))
	TargetAmount 	=	db.Column(db.Numeric(25, 9))
	FreqType 		= 	db.Column(db.String(1))
	Frequency 		= 	db.Column(db.String(3))
	PlanStatus 		=	db.Column(db.String(1)) # C=Close, O=Open
	SavingBalance 	=	db.Column(db.Numeric(25, 9))
	Term 			= 	db.Column(db.String(4))
	Cycle 			= 	db.Column(db.String(2))
	NextRunDate		=	db.Column(db.String(20))
	Currency 		= 	db.Column(db.String(3))
	FirstDate 		=	db.Column(db.String(15))
	InterestRate 	= 	db.Column(db.String(11))
	LoanID 		 	= 	db.Column(db.String(13))

