from app.mktcore.mdlimports import *

class MKT_SCHED_DEFINE(exmodel):
	ID 						= 	db.Column(db.String(13), primary_key=True, index=True)
	PrincipalFreq 			= 	db.Column(db.Integer)
	InterestFreq 			= 	db.Column(db.Integer)
	FwdBwdKey 				= 	db.Column(db.String(1))
	RepMode 				= 	db.Column(db.String(1))
	BaseDateKey 			= 	db.Column(db.String(1))
	FirstInstallmentDate 	= 	db.Column(db.String(20))
	#2018-07-13 payment-holiday
	PaymentHoliday 			= 	db.Column(db.String(1))
	NumOfHolidayInstallment = 	db.Column(db.String(4))
	WaiveInterest 			= 	db.Column(db.String(1))
	AnnuityAmount 			= 	db.Column(db.Numeric(25, 9))	
	DefaultAnnuityAmount	= 	db.Column(db.Numeric(25, 9))
	#end

