class MKT_SCHED_MANUAL(exmodel):
	ID 				= db.Column(db.String(21), primary_key=True, index=True)
	LoanID			= db.Column(db.String(13))
	CollectionDate	= db.Column(db.String(20))
	Type 			= db.Column(db.String(6))
	Principal 		= db.Column(db.Numeric(25, 9))
