from wtforms 						import DecimalField, IntegerField
from app.mktcore.wtfimports 		import *
from .models 						import *
import time

from app.Module.models 				import *

def loadForm():
	return MKT_MODULE.query

class FRM_APP_SETTING(exform):

	Description 	=	TextField(requiredlabel("Description", "*"), [validators.Required()])

	Application 	=	QuerySelectField(
							requiredlabel("Application", "*"),
							query_factory	= loadForm,
							get_label		= u'Description',
							allow_blank		= True,
							blank_text		= u'--Choose Application--',
							validators		= [validators.Required()]
						)

	Type 			=	SelectField(
							requiredlabel("Type", "*"),
							choices = [('I', 'Input Box'),
									   ('S', 'Select Box'),
									   ('T', 'TextArea'),
									   ('P', 'Password Box')],
							default = 'I',
							validators = [validators.Required()]
						)

	GroupBy			=	TextField("Group By")
	OrderBy			=	TextField("Order By")
	Value 			=	TextField("Value")
	LOV 			=	TextAreaField('List of Value')
	Definition 		=	TextAreaField(requiredlabel('Definition','*'),validators = [validators.Required()])