from app.mktcore.mdlimports import *


class MKT_STANDING_ORDER(exmodel):
	ID					=	db.Column(db.String(20), primary_key=True)
	DrCategory 			= 	db.Column(db.String(15))
	DrAccount 			=	db.Column(db.String(20))
	CrCategory 			= 	db.Column(db.String(15))
	CrAccount 			=	db.Column(db.String(20))
	Currency 			= 	db.Column(db.String(3))
	Amount 				= 	db.Column(db.Numeric(25, 9))
	OutstandingAmount 	=	db.Column(db.Numeric(25, 9))
	EarnedAmount		=	db.Column(db.Numeric(25, 9))
	Transaction 		= 	db.Column(db.String(3))
	Reference 			= 	db.Column(db.String(35))
	ValueDate 			=	db.Column(db.String(10))
	MaturityDate		=	db.Column(db.String(10))
	Note 				= 	db.Column(db.String(100))
	FrequencyType		=	db.Column(db.String(1))
	Frequency 			= 	db.Column(db.String(10))
	Amortize			=	db.Column(db.String(1))
	Terminate			=	db.Column(db.String(1))
	AutoReverse 		=	db.Column(db.String(1))
	BookAmount			=	db.Column(db.Numeric(25, 9))
	LastRunDate			= 	db.Column(db.String(10))
	NextRunDate 		= 	db.Column(db.String(10))

