from app.mktcore.mdlimports import *

class MKT_SYSTEM_HEALTH_CHECK(exmodel):
	ID 				= 		db.Column(db.Integer, db.Sequence('seq_reg_id', start=1, increment=1), primary_key=True)
	ItemName 		= 		db.Column(db.String(50))
	Title 			=		db.Column(db.String(100))
	SystemDate 		= 		db.Column(db.String(30))
	Status 			= 		db.Column(db.String(1))
	Level 			= 		db.Column(db.String(20))
	Code 			= 		db.Column(db.String(30))
	Description		=		db.Column(db.Text)
	ErrorDetail 	= 		db.Column(db.Text)
	Suggestion 		=		db.Column(db.String(1))



class MKT_SYSTEM_HEALTH_CHECK_HIST(exmodel):
	ID 				= 		db.Column(db.String(20), primary_key=True)
	ItemName 		= 		db.Column(db.String(50))
	Title 			=		db.Column(db.String(100))
	SystemDate 		= 		db.Column(db.String(30))
	Status 			= 		db.Column(db.String(1))
	Level 			= 		db.Column(db.String(20))
	Code 			= 		db.Column(db.String(30))
	Description		=		db.Column(db.Text)
	ErrorDetail 	= 		db.Column(db.Text)
	Suggestion 		=		db.Column(db.String(1))
