from app.mktcore.mdlimports import *

class MKT_TERM_DEPOSIT_CONTRACT(exmodel):

	ID 					=	db.Column(db.String(14), primary_key=True)
	CustomerID 			=	db.Column(db.String(12))
	Account 			=	db.Column(db.String(20))
	Currency 			=	db.Column(db.String(3))
	Amount 				=	db.Column(db.Numeric(25, 9))
	ValueDate 			=	db.Column(db.String(20))
	Term 				=	db.Column(db.String(4))
	MaturityDate 		=	db.Column(db.String(20))
	AccountProduct 		=	db.Column(db.String(3))
	AIPCat 				=	db.Column(db.String(15))
	IECat 				=	db.Column(db.String(15))
	InterestRate 		=	db.Column(db.String(5))
	IntPostingFreq 		=	db.Column(db.String(1))
	RollOver 			=	db.Column(db.String(1))
	RollOverOption 		=	db.Column(db.String(2))
	FixRate 			=	db.Column(db.String(1))
	PreClosingCharge 	=	db.Column(db.Numeric(25, 9))
	Cycle 				=	db.Column(db.String(2))

	AccrIntPerDay 		=	db.Column(db.Numeric(25, 9))
	IntPaid 			=	db.Column(db.Numeric(25, 9))
	AIPBooked 			=	db.Column(db.Numeric(25, 9))
	AIPCurrent 			=	db.Column(db.Numeric(25, 9))
	TotalAIP 			=	db.Column(db.Numeric(25, 9))
	NextRunDate 		=	db.Column(db.String(20))

