from app.mktcore.mdlimports import *

class MKT_TERM_DEPOSIT_PRODUCT(exmodel):

	ID 					=	db.Column(db.String(3), primary_key=True)
	Description 		=	db.Column(db.String(35))
	RollOver 			=	db.Column(db.String(1))
	RollOverOption 		=	db.Column(db.String(2))
	InterestKey 		=	db.Column(db.String(6))
	TARole 				=	db.Column(db.String(6))
	IntPostingFreq 		=	db.Column(db.String(1))
	InterestDayBasis 	=	db.Column(db.String(1))
	Tax 				=	db.Column(db.String(3))
	AIPCat 				=	db.Column(db.String(15))
	IECat 				=	db.Column(db.String(15))
	FixRate 			=	db.Column(db.String(1))
	PreClosingCharge 	=	db.Column(db.Numeric(25, 9))

