#class for table MKT_TILL_OPERATION
class MKT_TILL_OPERATION(exmodel):

	ID 			= db.Column(db.String(14), primary_key=True)
	CashID 		= db.Column(db.String(8))
	Operation 	= db.Column(db.String(1))
	Transaction = db.Column(db.String(3))
	TranDate 	= db.Column(db.String(20))
	Reference 	= db.Column(db.String(35))
	Note 		= db.Column(db.String(50))

