from app.mktcore.mdlimports import *

class MKT_USER_LOCATION(exmodel):

	ID 					= db.Column(db.Integer,  db.Sequence('MKT_USER_LOCATION_seq', start=1, increment=1),primary_key=True)
	UserID 				= db.Column(db.String(35))
	Role 				= db.Column(db.String(10))
	Branch 				= db.Column(db.String(10))
	LastTimeActivity 	= db.Column(db.DateTime)
	Latitude 			= db.Column(db.Text)
	Longitude 			= db.Column(db.Text)
	OnLogin				= db.Column(db.String(5))
