from .models 				import *
from app.User.models 		import *
from app 	 				import *
from app.tools.user 		import *
from app.mktcore.session   	import *
# from datetime 				import datetime
from flask 					import jsonify


@app.route("/Morakot/API/UserLocation", methods=['POST','GET'])
@checkLogOutSession
@checkLogOutTime
def userLocation():
	UserID 			= session['getLogInID']
	ObjUser 		= MKT_USER.query.filter_by(ID=UserID).first()
	Branch 			= aUser.getBranch().ID
	Result 			= saveUserLocation(UserID=UserID, Role=ObjUser.Role, Branch=Branch)
	if Result:
		return jsonify(status=200),200
	else:
		return jsonify(status=406),406