from app.mktcore.mdlimports import *

class MKT_VB(exmodel):
	ID = db.Column(db.String(10), primary_key=True)
	Description = db.Column(db.String(50))
	Coordinator = db.Column(db.String(50))
	Telephone = db.Column(db.String(15))
	# VBBranch = db.Column(db.String(6))
	VBProvince = db.Column(db.String(6))
	VBDistrict = db.Column(db.String(10))
	VBCommune = db.Column(db.String(14))
	VBVillage = db.Column(db.String(18))
	VBVillageII = db.Column(db.String(18))
	FreqType = db.Column(db.String(1))	
	Frequency = db.Column(db.String(1))		
	OfficerID = db.Column(db.String(10))

