class MKT_GROUP(exmodel):
	ID 					= db.Column(db.String(15), primary_key=True)
	VBID 				= db.Column(db.String(10))
	GroupName 			= db.Column(db.String(50))
	MinMember 			= db.Column(db.String(3))
	MaxMember 			= db.Column(db.String(3))
	Currency 			= db.Column(db.String(3))
	LoanProduct 		= db.Column(db.String(3))
	LoanPurpose 		= db.Column(db.String(3))
	Term 				= db.Column(db.String(4))
	Installment 		= db.Column(db.String(4))
	InterestRate 		= db.Column(db.String(11))
	FreqType 			= db.Column(db.String(1))
	Frequency 			= db.Column(db.String(3))
	ApprovedAmount 		= db.Column(db.Numeric(25, 9))
	OfficerID 			= db.Column(db.String(10))
	CompulsorySaving 	= db.Column(db.String(1))
	RateFixed 			= db.Column(db.String(1))
	CompulsoryRate 		= db.Column(db.String(10))
	VoluntarySaving 	= db.Column(db.String(1))
	TemplateBase 		= db.Column(db.String(12))
	VBDescription		= db.Column(db.String(50))

