from app.mktcore.mdlimports import *

class MKT_VENDOR(exmodel):
	__table_args__ = {'extend_existing': True}
	ID 						= db.Column(db.String(13), primary_key=True)
	VendorBranch			= db.Column(db.String(3))
	VendorName				= db.Column(db.String(100))
	VendorLocalName			= db.Column(db.String(100))
	DebitCategory			= db.Column(db.String(15))
	CreditCategory			= db.Column(db.String(15))
	VendorType				= db.Column(db.String(13))
	CorporationDate			= db.Column(db.String(10))
	MOUSign					= db.Column(db.String(1))
	Remark					= db.Column(db.String(150))
	VATRegistered			= db.Column(db.String(1))
	VATTIN					= db.Column(db.String(20))
	Mobile1					= db.Column(db.String(35))
	Mobile2					= db.Column(db.String(35))
	Email					= db.Column(db.String(35))
	Website					= db.Column(db.String(35))
	Facebook				= db.Column(db.String(50))
	LineID					= db.Column(db.String(35))
	WhatsApp				= db.Column(db.String(35))
	Telegram				= db.Column(db.String(35))
	Viber					= db.Column(db.String(35))
	Address 				= db.Column(db.String(100))
	Province 				= db.Column(db.String(50))
	District 				= db.Column(db.String(50))
	Commune 				= db.Column(db.String(50))
	Village 				= db.Column(db.String(50))

