registerCRUD(admin, '/Vendor', 'Vendor', FRM_VENDOR, [MKT_VENDOR])

@app.route("/Morakot/VATTINCondition")
def getVATTINCondition():
	
	Type 		= 	request.args.get('VATRegistered')

	Condition 	= False

	if Type == "N":
		Condition = True

	return jsonify(Condition = Condition)
