from app.mktcore.wtfimports import *
from .models import *
from .. import app, db
import time

class FRM_VOUCHER_TYPE(exform):
	Description = TextField(requiredlabel("Description", "*"), [validators.Required()])

	def validate_Description(form, field):
		QueryObj = MKT_VOUCHER_TYPE.query.filter_by(Description=form.Description.data).first()
		if QueryObj:
			raise ValidationError("The description is already exist")