from app.mktcore.wtfimports import *
from .models import *


class FRM_APPROVAL_SETTING(exform):
	
	Description 	= TextField(requiredlabel("Description", "*"), [validators.Required(), validators.length(max=35)])
	EditMode		= SelectField('Edit Mode',choices=[('', '--None--'),('False', 'No'),('True', 'Yes')],coerce=str)
	Enable			= SelectField('Enable',choices=[('1', 'Yes'),('2', 'No')],coerce=str)
	ExtraRole  		= TextField(("Extra role to get notification "))
	Url  			= TextField(requiredlabel("Form Url","*"),validators=[validators.Required()])
	Models 			= TextAreaField(requiredlabel("Models","*"),validators=[validators.Required()])
	LogicRule       = TextAreaField(requiredlabel("Logic and Rule","*"),validators=[validators.Required(),validators.length(max=300)])
