import os,time


# Include version auto from file
VersionPath = os.path.dirname(os.path.abspath(__file__)) + "/VERSION.txt"
with open(VersionPath, "r") as fh:
    version = fh.read()

modified = os.path.getmtime(VersionPath)
DateModified = time.ctime(modified)

__Version__	= 'v%s'%version
__Build__	= modified
__Release__	= DateModified


