import app.tools.mktsetting as mktsetting 

def getReAssignRoleSetting():

	"""
	- get user role that have permission to re-assign to other role. we read it from setting configuration.
	- return dict
		return formula: {NumRole:ListOfAsssigneeRole}
		return example:{99:[2,3,4,5],
						98:[10,11,12]}

	"""
	ReAssingRole 		= mktsetting.getAppSetting('AssigneeRole')
	DictReAssignRole 	= {}
	if ReAssingRole:
		ReAssingRole = ReAssingRole.replace('\r','')
		ReAssingRole = ReAssingRole.split('\n')
		for Row in ReAssingRole:
			Row = Row.split('*')
			Assignee = Row[1].split() if len(Row)>1 else []
			DictReAssignRole.update({Row[0]:Assignee})
	return DictReAssignRole