def loadPosition():
	return MKT_POSITION.query
def loadStaff(ID=''):
	return MKT_CUSTOMER.query.filter(MKT_CUSTOMER.Staff =='Y')


@app.route("/Morakot/RelationParty", methods=['GET'])
def getRelationParty():
	dic = False
	Choices = request.args.get('Staff') 
	app.logger.debug(Choices)
	if Choices != "N":
		dic = True
	return jsonify(Bool=dic)


