from app.mktcore.imports 	import *
from .detail 				import *

registerCRUD(admin, '/CollateralDetailEx', 'CollateralDetailEx', FRM_COLLATERAL_DE_EX, [MKT_COLLATERAL_DE])

@app.route("/Morakot/getLTVPercentage", methods=['GET'])
@checkLogOutSession
@checkLogOutTime
def getLTVPercentage():
	LTV = 0
	CollateralType = request.args.get('CollateralType')
	CollateralTypeObj = MKT_COLLATERAL_TYPE.query.get(CollateralType)
	if CollateralTypeObj:
		if CollateralTypeObj.LTV==None:
			LTV = 0
		else:
			LTV = CollateralTypeObj.LTV
	return jsonify(LTV = LTV)