from app.mktcore.mdlimports import *

class MKT_FOLLOWUP_CARD(exmodel):
	ID 				 = db.Column(db.String(13), primary_key=True, index=True)
	CustomerID 		 = db.Column(db.String(13))
	NextFollowUpDate = db.Column(db.String(13))
	AmendDate 		 = db.Column(db.String(30))
	FreqMonth 		 = db.Column(db.String(15))
	AutoCardID 		 = db.Column(db.String(15))
	LoanID 			 = db.Column(db.String(15))
	ActionPlan 		 = db.Column(db.Text())
	NextReviewType 	 = db.Column(db.String(5))
	Description 	 = db.Column(db.Text())


