from app.mktcore.mdlimports import *

class MKT_LOAN_CANCELLATION(exmodel):
	ID 				= db.Column(db.String(13), primary_key=True)
	CustomerID 		= db.Column(db.String(12))
	LoanAppID 		= db.Column(db.String(13))
	LoanID 			= db.Column(db.String(13))
	Currency 		= db.Column(db.String(3))
	CanceledAmount = db.Column(db.Numeric(25, 9))
	CancellationType= db.Column(db.String(3))
	Reason 			= db.Column(db.String(150))

