from app.LoanWriteOff.WriteOffDetails 						import *

class FRM_WRITE_OFF_DE_EX(FRM_WRITE_OFF_DE):
	"""Derived class extended from FRM_WRITE_OFF_DE"""
	TotWOFAmount 	=	TextField("Total WO Amount to be Recovered")
	AddonAmount 	= 	TextField("Total Insurance Amount")
	ValueDate 		=	TextField("Loan Value Date")
	MaturityDate 	=	TextField("Loan Maturity Date")
	WOFDate 		=	TextField("Write-Off Date")
	DefaultDate 	=	TextField("Default Date")
	Officer 		=	TextField("Officer ID")	
	LagacyID 		=	TextField("Lagacy ID")
	
	@staticmethod
	def setDisable():
		base = super(FRM_WRITE_OFF_DE_EX, FRM_WRITE_OFF_DE_EX).setDisable()
		controls_list 	= [("AddonAmount")]
		controls_list.extend(base) if base else controls_list
		return controls_list

	@staticmethod
	def setDisableforEdit():  # override base class method 
		base = super(FRM_WRITE_OFF_DE_EX, FRM_WRITE_OFF_DE_EX).setDisable()
		controls_list 	= [("AddonAmount")]
		controls_list.extend(base) if base else controls_list
		return controls_list

	@staticmethod
	def listField():
		return ["ID", "CustomerID", "CustomerName", "AccountNumber", "Currency", "WOFDate",
				"LoanDisbursed", "LoanBalance", "TotPrincipalDue", "TotInterestDue",
				"TotWOFAmount","LagacyID"]