from app.mktcore.mdlimports import *

class MKT_PO_CUSTOMER_RECONTACT(exmodel):
	ID 					=	db.Column(db.String(12),primary_key=True)
	PotentialCustomer 	= 	db.Column(db.String(12))
	POStatus			=	db.Column(db.String(3))
	AssignTo			=	db.Column(db.String(35))	
	ExistingClient		= 	db.Column(db.String(1))
	Currency 			= 	db.Column(db.String(3))
	LoanAmount			= 	db.Column(db.Numeric(25, 9))
	ProductInterest 	=	db.Column(db.String(3))
	DateOfContact		=	db.Column(db.String(10))
	Mobile1 			=	db.Column(db.String(15))
	Note 				=	db.Column(db.String(100))

