# MKT_SCHEDULE_ADDON_PAYMENT Main Model
class MKT_SCHEDULE_ADDON_PAYMENT(exmodel):
	ID 				= db.Column(db.String(13), primary_key = True, index=True)
	CustomerID 		= db.Column(db.String(12))
	LoanID 			= db.Column(db.String(20))
	Currency 		= db.Column(db.String(3))
	StartDate 		= db.Column(db.String(10))
	EndDate 		= db.Column(db.String(10))
	TranDate 		= db.Column(db.String(10))
	AddonPaymentType= db.Column(db.String(3))
	Amount	 		= db.Column(db.Numeric(25,9))
	Reference 		= db.Column(db.String(35))
	Note	 		= db.Column(db.Text)
	ExchangeAmount	= db.Column(db.Numeric(25,9))

