from app.mktcore.wtfimports 	import *

from app.IdType.models 			import *

from app.tools.mktroute 		import *

from local.FirstFinance.SurveyAnswer.models 	import *

import app.tools.mktdb 			as mktdb

def loadAnswer():
	Answer = MKT_SURVEY_ANSWER.query
	return Answer

class FRM_ANSWER_DE(exform):
	Answer = QuerySelectField(
								  'Answer Type',
								  query_factory=loadAnswer,
								  get_label=lambda a: a.ID + " - " + a.Description,
								  allow_blank=True,
								  blank_text="Choose Answer Type"
								)
	Description = TextField(requiredlabel('Answer','*'),validators=[validators.Required()])
