from app.mktcore.wtfimports 	import *

from app.IdType.models 			import *

from app.tools.mktroute 		import *

from local.FirstFinance.SurveyAnswer.models 		import *

import app.tools.mktdb 			as mktdb


def loadAnswerType():
	AnswerType = MKT_SURVEY_ANSWER.query

	return AnswerType


class FRM_SURVEYQUESTION(exform):

	AnswerType 		= QuerySelectField(
								  'Answer Type',
								  query_factory=loadAnswerType,
								  get_label=lambda a: a.ID + ' - ' + a.Description,
								  allow_blank=True,
								  blank_text="Choose Type of Answer"
							)

	Description = TextField(requiredlabel('Question','*'),validators=[validators.Required()])

