from app.FundTransfer.models 	import *

class ExtendModuleEx(object):
	def getDashboardUnauth(self):

		IPCondition = [MKT_FUND_TRANSFER_INAU.Module=='IP']
		EPCondition = [MKT_FUND_TRANSFER_INAU.Module=='EP']

		""" Syntax = { 'Label': ['URL', 'TABLE', 'CONDITION']}"""
		DashboardConf 	= {
					'Income Posting':							['IncomePostingEx',						'MKT_FUND_TRANSFER_INAU',			IPCondition],
					'Expense Posting':							['ExpensePostingEx',					'MKT_FUND_TRANSFER_INAU',			EPCondition],
					'Customer':									['CustomerEx',							'MKT_CUSTOMER_INAU']
		}
		
		return DashboardConf

	def setListUrlExtend(self):
		""" Syntax = { "OLD_URL" : ['NEW_URL','TITLE']} """
		ListUrl = {
					'IncomePosting':							['IncomePostingEx',						'Income Posting'],
					'ExpensePosting':							['ExpensePostingEx',					'Expense Posting'],
					'Customer':									['CustomerEx',							'Customer']
				}
		return ListUrl

	def setConfigNotification(self):
		""" Syntax = {'URL' : 'TITLE'}"""
		ConfigNotification = {
								'IncomePostingEx'				: 'Income Posting',
								'ExpensePostingEx'				: 'Expense Posting',
								'CustomerEx'					: 'Customer'
							}
		return ConfigNotification
