from app.mktcore.mdlimports import *

class MKT_DEALER(exmodel):
	ID 						= db.Column(db.String(20), primary_key=True)
	DealerName 				= db.Column(db.String(50))
	DealerKhmerName 		= db.Column(db.String(50))
	ContactNumber 			= db.Column(db.String(50))
	Province 				= db.Column(db.String(50))
	District 				= db.Column(db.String(50))
	Commune 				= db.Column(db.String(50))
	Village 				= db.Column(db.String(50))
	Remark 					= db.Column(db.String(150))

