from app.mktcore.mdlimports import *

#MKT_DOWN_PAYMENT
class MKT_DOWN_PAYMENT(exmodel):
	ID              =   db.Column(db.String(13), primary_key=True)
	LoanID			=   db.Column(db.String(13))
	Currency 		= 	db.Column(db.String(3))
	AssetPrice     	=   db.Column(db.Numeric(25,9))
	DownPayment     =   db.Column(db.Numeric(25,9))
	LoanAmount     	=   db.Column(db.Numeric(25,9))
	InvoiceNo      	=   db.Column(db.String(50))
	PurchaseInvoiceNo = db.Column(db.String(50))
	Reference 		= 	db.Column(db.String(100))
