from app.mktcore.mdlimports import *


class MKT_VENDOR(exmodel):
	"""docstring for MKT_VENDOR"""
	ID 				= db.Column(db.String(12), primary_key=True)
	VendorName		= db.Column(db.String(30))
	ContactPerson 	= db.Column(db.String(30))
	Mobile1 		= db.Column(db.String(15))
	Mobile2 		= db.Column(db.String(15))
	Email 			= db.Column(db.String(50))
	Province        = db.Column(db.String(3))
	District        = db.Column(db.String(5))
	Commune         = db.Column(db.String(7))
	Village         = db.Column(db.String(9))
	HouseNo         = db.Column(db.String(50))
	Street          = db.Column(db.String(40))

