from app import db
from app.LoanApplication.models 	import *
from local.IFinance.Dealer.models import *

def getDealerInformation(args):
	LoanAppID = args[0]
	DealerName = ''
	DealerObj = db.session.query(MKT_DEALER.DealerName).\
				join(MKT_LOAN_APPLICATION,MKT_LOAN_APPLICATION.ID==LoanAppID).\
				join(MKT_DEALER,MKT_DEALER.ID==MKT_LOAN_APPLICATION.DealerName).\
				first()
	if DealerObj:
		DealerName = DealerObj.DealerName
	return DealerName