from app.mktcore.mdlimports import *

class MKT_VB_SUB_CYCLE(exmodel):
	ID 					= db.Column(db.String(13), primary_key=True)
	VillageBankCycle 	= db.Column(db.String(13))
	VillageBank 		= db.Column(db.String(13))
	PeriodCourse 		= db.Column(db.String(3))
	DateOpen 			= db.Column(db.String(10))
	DateClose 			= db.Column(db.String(10))
	GroupTotal 			= db.Column(db.String(3))
	MemberTotal 		= db.Column(db.String(3))
	BorrowerTotal 		= db.Column(db.String(3))
	NewBorrowerTotal 	= db.Column(db.String(3))
	Currency  			= db.Column(db.String(3))
	AmountTotal 		= db.Column(db.Numeric(25,9))
	AmountTotalChar 	= db.Column(db.String(100))
	FirstPayDate 		= db.Column(db.String(10))


