from app.FundTransfer.models 		import MKT_FUND_TRANSFER_INAU
from app.Teller.models 				import MKT_TELLER_INAU
from ..ForeclosedProperty.models 	import MKT_FORECLOSED_PROPERTY_INAU
	

class ExtendModuleEx(object):
	def getDashboardUnauth(self):
		""" Syntax = { 'Label': ['URL', 'TABLE', 'CONDITION']}"""
		SDCondition 	= [MKT_FUND_TRANSFER_INAU.ID.like('%'+'SD'+'%')]
		FPTCondition 	= [MKT_FORECLOSED_PROPERTY_INAU.FCStatus=='1']
		FPSondition 	= [MKT_FORECLOSED_PROPERTY_INAU.FCStatus=='2']
		FPRCondition 	= [MKT_FORECLOSED_PROPERTY_INAU.FCStatus=='3']
		DPCondition 	= [MKT_TELLER_INAU.ID.like('%'+'DP'+'%')]
		DashboardConf 	= {
				'Loan Contract':					['LoanContractEx',					'MKT_LOAN_CONTRACT_INAU'							],
				'Supplier': 						['Supplier', 						'MKT_SUPPLIER_INAU'									],
				'Deposit':							['DepositEx',						'MKT_TELLER_INAU',					DPCondition 	],
				'Security Deposit': 				['SecurityDeposit', 				'MKT_FUND_TRANSFER_INAU',			SDCondition 	],
				'Transfer Foreclosed Property': 	['TransferForeclosedProperty', 		'MKT_FORECLOSED_PROPERTY_INAU',		FPTCondition 	],
				'Sale Foreclosed Property'    : 	['SaleForeclosedProperty', 			'MKT_FORECLOSED_PROPERTY_INAU',		FPSondition 	],
				'Revalue Foreclosed Property' : 	['RevalueForeclosedProperty', 		'MKT_FORECLOSED_PROPERTY_INAU',		FPRCondition 	]
			}

		return DashboardConf

	def setListUrlExtend(self):
		""" Syntax = { "OLD_URL" : ['NEW_URL','TITLE']} """
		ListUrl = {	
					'LoanContract':				['LoanContractEx',						'Loan Contract'				],
					'Deposit':					['DepositEx',							'Deposit'					],
					'SecurityDeposit':			['SecurityDeposit',						'SecurityDeposit'			]
				}
		return ListUrl

	def setConfigNotification(self):
		""" Syntax = {'URL' : 'TITLE'}"""
		ConfigNotification = {	'LoanContractEx'			: 'Loan Contract',
								'DepositEx' 				: 'Deposit',
								'Supplier' 					: 'Supplier',
								'SecurityDeposit'			: 'Security Deposit',
								'TransferForeclosedProperty': 'Transfer Foreclosed Property',
								'SaleForeclosedProperty'	: 'Sale Foreclosed Property',
								'RevalueForeclosedProperty' : 'Revalue Foreclosed Property'
								}
		return ConfigNotification
