from app.mktcore.mdlimports import *

class MKT_GUARANTEE_LIST(exmodel):
	ID 				= db.Column(db.String(25), primary_key=True, index=True)
	LoanID			= db.Column(db.String(15))
	Currency		= db.Column(db.String(3))
	EngineNo		= db.Column(db.String(15))
	ChassieNo		= db.Column(db.String(20))
	Brand			= db.Column(db.String(50))
	Year			= db.Column(db.String(4))
	Size			= db.Column(db.String(7))
	MarketPrice		= db.Column(db.Numeric(25, 9))
	SPA				= db.Column(db.String(1))
	PlateNo			= db.Column(db.String(15), index=True)
	Tax				= db.Column(db.Numeric(25, 9))
	LendingAmt		= db.Column(db.Numeric(25, 9))
	Outstanding		= db.Column(db.Numeric(25, 9))
	LTV				= db.Column(db.Numeric(25, 9))
	ChqIssue		= db.Column(db.String(50))
	Description		= db.Column(db.String(50))


