from app.mktcore.mdlimports import *

class MKT_REDEM_COLLATERAL(exmodel):
	ID 			= db.Column(db.String(20), primary_key=True, index=True)
	LoanID		= db.Column(db.String(15))
	AmendID		= db.Column(db.String(13))
	SubLoanID	= db.Column(db.String(25))
	CustomerName= db.Column(db.String(50))
	Currency	= db.Column(db.String(3))
	AmendDate	= db.Column(db.String(10))
	Outstanding	= db.Column(db.Numeric(25, 9))
	Principle	= db.Column(db.Numeric(25, 9))
	NumberOfDay	= db.Column(db.String(4))
	InterestDue	= db.Column(db.Numeric(25, 9))
	Description	= db.Column(db.String(50))

